body.raw:
{
  "query": "mutation Register($email: String!, $password: String!, $fullName: String!, $phone: String) { register(email: $email, password: $password, fullName: $fullName, phone: $phone) { accessToken refreshToken user { id email fullName } } }",
  "variables": {
    "email": "test@example.com",
    "password": "123456",
    "fullName": "Test User",
    "phone": "0909000000"
  }
}

{
  "query": "mutation Login($email: String!, $password: String!) { login(email: $email, password: $password) { accessToken refreshToken user { id email fullName } } }",
  "variables": {
    "email": "test@example.com",
    "password": "123456"
  }
}

{
  "query": "mutation ($title: String!, $slug: String) { createWedding(title: $title, slug: $slug) { id title slug status } }",
  "variables": {
    "title": "My Wedding",
    "slug": "my-wedding"
  }
}

{
  "query": "query ($id: ID!) { wedding(id: $id) { id title slug status weddingDetail { bride { fullName } } } }",
  "variables": {
    "id": "WEDDING_ID"
  }
}

{
  "query": "mutation ($weddingId: ID!, $bride: BrideGroomInput!) { updateBride(weddingId: $weddingId, bride: $bride) { id bride { fullName shortBio } } }",
  "variables": {
    "weddingId": "WEDDING_ID",
    "bride": {
      "fullName": "Jane Doe",
      "shortBio": "The bride"
    }
  }
}

{
  "query": "mutation ($weddingId: ID!, $event: WeddingEventInput!) { addWeddingEvent(weddingId: $weddingId, event: $event) { weddingEvents { id title eventDate } } }",
  "variables": {
    "weddingId": "WEDDING_ID",
    "event": {
      "title": "Ceremony",
      "type": "CEREMONY",
      "eventDate": "2025-01-01",
      "address": "Wedding Hall"
    }
  }
}

{
  "query": "mutation ($weddingId: ID!, $rsvp: RSVPInput!) { submitRSVP(weddingId: $weddingId, rsvp: $rsvp) { id fullName attendanceStatus } }",
  "variables": {
    "weddingId": "WEDDING_ID",
    "rsvp": {
      "fullName": "Guest Name",
      "numberOfGuests": 2,
      "attendanceStatus": "CONFIRMED"
    }
  }
}

{
  "query": "mutation ($weddingId: ID!, $wish: WishInput!) { addWish(weddingId: $weddingId, wish: $wish) { id guestName message } }",
  "variables": {
    "weddingId": "WEDDING_ID",
    "wish": {
      "guestName": "Anonymous",
      "message": "Congratulations!"
    }
  }
}
